document.addEventListener("DOMContentLoaded",()=>{!function(){const host=window.location.host,buttonContainer=document.querySelectorAll(".text-with-images__images");if(null!=buttonContainer){let imgData={};buttonContainer.forEach(bc=>{let buttons=bc.querySelectorAll("button.js-fullscreen");buttons.forEach((button,i)=>{2!=buttons.length?adCl="text-with-images__images__full-width-button":adCl="",imgData={lowImage:button.firstElementChild.src.replace("https://"+host,""),hiImage:button.dataset.image,alt:button.dataset.alt,i:i+1,cl:adCl};let template=`<button class="js-fullscreen gallery-5093 ${imgData.cl}" tabindex="0"\n\t                data-image="${imgData.hiImage}"\n    \t            data-description=""\n        \t        data-alt="${imgData.alt}" data-start="${imgData.i}"\n            \t    data-close="Zamknij" data-gallery=".gallery-5093">\n                \t<a href="${imgData.hiImage}" class="glightbox" data-title="${imgData.alt}">\n                    <img src="${imgData.lowImage}"\n                        alt="${imgData.alt}"\n                        class="text-with-images__images__image">\n                \t</a>\n            \t\t</button>`,buttonParent=button.parentElement;button.remove(),buttonParent.insertAdjacentHTML("beforeend",template)})})}GLightbox({touchNavigation:true,loop:true,autoplayVideos:true})}()});